import React, { useState } from 'react'
import "./style.css"
import { useParams } from 'react-router-dom'

const Blog = () => {
    const [loading, setLoading] = useState(false)
    const {id} = useParams();
    const [blog, setBlog] = useState()
  return (
    <div className='blog-wrapper'>
      {
        loading ?
        <div> Loading... </div>
        :
        <div className='single-blog'>
            <b>{blog?.title}</b>
            <p>{blog?.desc}</p>
            </div>
      }
    </div>
  )
}

export default Blog
