    const [courses, setCourses] = useState([])
    const api = makeApiRequest();

    useEffect(()=>{
        const getRandomCourses = async () =>{
            try{
                const response = await api.get(`/api/v1/courses/all?show=${true}`)
                console.log(response)
            }catch(err){
                console.log(err)
            }
        }
        getRandomCourses()
    },[])

