import { useEffect, useState } from "react"
import Content from "../../components/content/Content"
import Footer from "../../components/footer/Footer"
import "./homepage.css"
import axios from "axios"

const Homepage = () => {
    const [registerDetails, setRegisterDetails] = useState({
        username:"",
        email:"",
        number:"",
        password:""
    })

    const user = false;

    useEffect(()=>{
        const form = document.getElementById("form");
        !user && form.scrollIntoView({behavior:"smooth"})
    },[user])

    const handleChnage = (e) =>{
        const {name, value} = e.target;
        setRegisterDetails((pre)=>{
            return {
                ...pre, [name]:value
            }
        })
    }

    const handleRegister = async (e) =>{
        e.preventDefault()
        try{
            const response = await axios.post(`${process.env.REACT_APP_URI}api/v1/auth/register`,registerDetails)
            setRegisterDetails({
                username: "",
                email: "",
                number: "",
                password: ""
            });
        }catch(err){
            console.log(err)
        }
    }

    return (
        <>
        <div className="homepage">
            {
                !user ?
                <form id="form">
                <span className="notice">signUp now !</span>
                    <span className="title">Join Your Favorite Free Course Today!</span>
                    <span className="desc">Learn with engaging and entertaining examples that transform the learning process into an enjoyable and effortless experience.</span>
                    <b>Enter Your Details</b>
                    <input type="text" value={registerDetails.username} name="username" placeholder="enter your name" onChange={handleChnage}></input>
                    <input type="email" value={registerDetails.email} name="email" placeholder="enter your email" onChange={handleChnage}></input>
                    <input type="text" value={registerDetails.number} name="number" placeholder="enter your mobile number" onChange={handleChnage}></input>
                    <input type="password" value={registerDetails.password} name="password" placeholder="enter your password" onChange={handleChnage}></input>
                    <select>
                        <option value="">Select Your State</option>
                        <option value="">uttrakhand</option>
                        <option value="">haryana</option>
                    </select>
                    <button className="signup" onClick={handleRegister}>SignUp to continue</button>
                </form>
                :
                <div> 
                    Lorem ipsum dolor, sit amet consectetur adipisicing elit. Est necessitatibus, numquam expedita, eligendi quas mollitia non pariatur temporibus cupiditate quidem porro obcaecati odio, repudiandae ratione vitae a rem debitis. Quisquam excepturi, laboriosam corporis id odio expedita! Accusamus dolorem dolores perspiciatis?
                </div>
            }
        </div>
        <Content />
        <Footer />
        </>
    )
}


export default Homepage
